      out.write("\t\tvar monthServiceFeeRate = Number(parseFloat($(\"#mocounselingRate\").val()));\r\n");
      out.write("\t\t// 利率\r\n");
      out.write("\t\tvar loanInterestRate = Number($(\"#moloanRate\").val());\r\n");
      out.write("\t\t// 信访费用\r\n");
      out.write("\t\tvar visitFee = Number($(\"#movisitFee\").val());\r\n");
      out.write("\t\t// 计算金额\r\n");
      out.write("\t\tif (contractLoanAmount != \"\" && loanPeriodType != \"\"\r\n");
      out.write("\t\t\t\t&& monthServiceFeeRate != \"\" && loanInterestRate != \"\"\r\n");
      out.write("\t\t\t\t&& visitFee != \"\") {\r\n");
      out.write("\t\t\t// 实放金额 = 合同金额-(合同金额*(服务汇率*贷款期限)+信访费用)\r\n");
      out.write("\t\t\tvar actualLoanAmount = contractLoanAmount\r\n");
      out.write("\t\t\t\t\t- (contractLoanAmount\r\n");
      out.write("\t\t\t\t\t\t\t* (monthServiceFeeRate * loanPeriodType) + visitFee);\r\n");
      out.write("\t\t\t// 月还款额 = (合同金额/贷款期限)+合同金额*利息\r\n");
      out.write("\t\t\tvar monthRepay = (contractLoanAmount / loanPeriodType)\r\n");
      out.write("\t\t\t\t\t+ contractLoanAmount * loanInterestRate;\r\n");
